mapper.select:
SELECT BBS_SEQ, BBS_SUB_CATE_COMBINED_ID, BBS_TITLE, BBS_CONTENT, REG_DATE, UPDATE_DATE, BBS_READ_CNT, BBS_STATUS, USER_EMAIL, USER_NAME, BBS_SUB_CATE_NAME, BBS_MAIN_CATE_ID, BBS_MAIN_CATE_NAME, BBS_IMG_NAME, HEART FROM( SELECT ROWNUM RNUM, BBS_SEQ, BBS_SUB_CATE_COMBINED_ID, BBS_TITLE, BBS_CONTENT, REG_DATE, UPDATE_DATE, BBS_READ_CNT, BBS_STATUS, USER_EMAIL, USER_NAME, BBS_SUB_CATE_NAME, BBS_MAIN_CATE_ID, BBS_MAIN_CATE_NAME, BBS_IMG_NAME, HEART FROM ( SELECT A.BBS_SEQ, NVL(C.BBS_SUB_CATE_COMBINED_ID, '') BBS_SUB_CATE_COMBINED_ID, NVL(A.BBS_TITLE, '') BBS_TITLE, NVL(A.BBS_CONTENT, '') BBS_CONTENT, NVL(TO_CHAR(A.REG_DATE,'YYYY.MM.DD HH24:MI:SS'), '') REG_DATE, NVL(TO_CHAR(A.UPDATE_DATE,'YYYY.MM.DD HH24:MI:SS'), '') UPDATE_DATE, NVL(A.BBS_READ_CNT, 0) BBS_READ_CNT, NVL(A.BBS_STATUS, '') BBS_STATUS, NVL(A.USER_EMAIL, '') USER_EMAIL, NVL(D.USER_NAME, '') USER_NAME, NVL(C.BBS_SUB_CATE_NAME, '') BBS_SUB_CATE_NAME, NVL(B.BBS_MAIN_CATE_ID, '') BBS_MAIN_CATE_ID, NVL(B.BBS_MAIN_CATE_NAME, '') BBS_MAIN_CATE_NAME, (SELECT MIN(BBS_IMG_NAME) FROM BBS_IMG WHERE BBS_SEQ = A.BBS_SEQ) BBS_IMG_NAME, CASE WHEN EXISTS ( SELECT 1 FROM BBS_WISHLIST W WHERE W.BBS_SEQ = A.BBS_SEQ AND W.USER_EMAIL = #{userEmail} ) THEN 'Y' ELSE 'N' END AS HEART FROM BBS A,BBS_MAIN_CATE B,BBS_SUB_CATE C ,TBL_USER D WHERE BBS_STATUS = 'Y'
AND A.USER_EMAIL = D.USER_EMAIL AND A.BBS_SUB_CATE_COMBINED_ID = C.BBS_SUB_CATE_COMBINED_ID AND B.BBS_MAIN_CATE_ID = C.BBS_MAIN_CATE_ID AND A.BBS_SUB_CATE_COMBINED_ID LIKE '%' || #{bbsSubCateCombinedId} || '%'
ORDER BY A.BBS_SEQ DESC ) ) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

SELECT USER_EMAIL
FROM BBS
WHERE BBS_SEQ = #{bbsSeq}
