mapper.select:
select 
    user_email 
from 
    tbl_user
where 
    user_name = #{userName}
    and user_tel=#{userTel}
    and user_jumin=#{userJumin}

select 
    user_pwd
from
    tbl_user
where
    user_email = #{userEmail}
and
    user_tel = #{userTel}
and
    user_jumin = #{userJumin}

mapper.update:
UPDATE
    tbl_user
    set user_pwd = #{userPwd}
where
    user_email = #{userEmail}

UPDATE
    tbl_user
    set user_name = #{userName}
where
    user_email = #{userEmail}

UPDATE
    tbl_user
    set user_tel = #{userTel}
where
    user_email = #{userEmail}

UPDATE
    tbl_user
    set USER_PROFILE_IMG = #{userProfileImg}
where
    user_email = #{userEmail}

UPDATE
    tbl_user
    set USER_PROFILE_INTRO = #{userProfileIntro}
where
    user_email = #{userEmail}

UPDATE
    tbl_user
    set USER_status = #{userStatus}
where
    user_email = #{userEmail}

    	UPDATE shipping_addr
		SET SHIPPING_ADDR_STATUS = 'N', is_default = 'N'
		WHERE shipping_addr_num = #{shippingAddrNum}
		AND user_email = #{userEmail}
		AND SHIPPING_ADDR_STATUS = 'Y'

    	UPDATE shipping_addr
		SET is_default = 'Y'
		WHERE shipping_addr_num = #{shippingAddrNum}
		AND user_email = #{userEmail} 
		AND SHIPPING_ADDR_STATUS = 'Y'

mapper.select:
SELECT is_default
FROM shipping_addr
WHERE shipping_addr_num = #{shippingAddrNum} 
  AND user_email = #{userEmail} 
  AND SHIPPING_ADDR_STATUS = 'Y'
