mapper.select:
SELECT BBS_SEQ, BBS_SUB_CATE_COMBINED_ID, BBS_TITLE, BBS_CONTENT, REG_DATE, UPDATE_DATE, BBS_READ_CNT, BBS_STATUS, USER_EMAIL, USER_NAME, BBS_SUB_CATE_NAME, BBS_MAIN_CATE_ID, BBS_MAIN_CATE_NAME FROM( SELECT ROWNUM RNUM, BBS_SEQ, BBS_SUB_CATE_COMBINED_ID, BBS_TITLE, BBS_CONTENT, REG_DATE, UPDATE_DATE, BBS_READ_CNT, BBS_STATUS, USER_EMAIL, USER_NAME, BBS_SUB_CATE_NAME, BBS_MAIN_CATE_ID, BBS_MAIN_CATE_NAME FROM ( SELECT A.BBS_SEQ, NVL(C.BBS_SUB_CATE_COMBINED_ID, '') BBS_SUB_CATE_COMBINED_ID, NVL(A.BBS_TITLE, '') BBS_TITLE, NVL(A.BBS_CONTENT, '') BBS_CONTENT, NVL(A.REG_DATE, '') REG_DATE, NVL(A.UPDATE_DATE, '') UPDATE_DATE, NVL(A.BBS_READ_CNT, 0) BBS_READ_CNT, NVL(A.BBS_STATUS, '') BBS_STATUS, NVL(A.USER_EMAIL, '') USER_EMAIL, NVL(D.USER_NAME, '') USER_NAME, NVL(C.BBS_SUB_CATE_NAME, '') BBS_SUB_CATE_NAME, NVL(B.BBS_MAIN_CATE_ID, '') BBS_MAIN_CATE_ID, NVL(B.BBS_MAIN_CATE_NAME, '') BBS_MAIN_CATE_NAME FROM BBS A, BBS_MAIN_CATE B, BBS_SUB_CATE C, TBL_USER D WHERE A.BBS_SUB_CATE_COMBINED_ID = C.BBS_SUB_CATE_COMBINED_ID AND B.BBS_MAIN_CATE_ID = C.BBS_MAIN_CATE_ID AND A.USER_EMAIL = D.USER_EMAIL AND A.BBS_STATUS = 'Y' AND A.USER_EMAIL = #{userEmail}
AND A.USER_EMAIL LIKE '%' || #{searchValue} || '%'
AND A.BBS_TITLE LIKE '%' || #{searchValue} || '%'
AND DBMS_LOB.INSTR(A.BBS_CONTENT, #{searchValue}) > 0
AND NVL(C.BBS_SUB_CATE_COMBINED_ID, '') LIKE '%' || #{bbsSubCateCombinedId} || '%'
ORDER BY BBS_SEQ DESC)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

SELECT bbs_seq, bbs_sub_cate_combined_id, bbs_title, bbs_content, reg_date, update_date, bbs_read_cnt, bbs_status, user_name from (SELECT rownum rnum, bbs_seq, bbs_sub_cate_combined_id, bbs_title, bbs_content, reg_date, update_date, bbs_read_cnt, bbs_status, user_name from (SELECT b.bbs_seq as bbs_seq, b.bbs_sub_cate_combined_id as bbs_sub_cate_combined_id, b.bbs_title as bbs_title, b.bbs_content as bbs_content, to_char(b.reg_date , 'yyyy.mm.dd') as reg_date, to_char(b.update_date,'yyyy.mm.dd') as update_date, b.bbs_read_cnt as bbs_read_cnt, b.bbs_status as bbs_status, u.user_name as user_name FROM bbs b JOIN tbl_user u on b.user_email = u.user_email join BBS_SUB_CATE S ON b.bbs_sub_cate_combined_id = s.bbs_sub_cate_combined_id where 1 = 1 AND b.bbs_sub_cate_combined_id = 'ADAC' AND b.bbs_status = 'Y'
AND b.bbs_title LIKE '%' || #{searchValue} || '%'
order by b.bbs_seq desc)) where rnum BETWEEN #{startRow} and #{endRow}
